import React, { useState } from 'react';
import ImageUploader from './components/ImageUploader';
import TaskStatus from './components/TaskStatus';
import './styles.css';

function ErrorBoundary({ children }) {
  const [hasError, setHasError] = useState(false);

  if (hasError) {
    return <h2>Something went wrong. Please try again later.</h2>;
  }

  return (
    <React.Fragment>
      {children}
    </React.Fragment>
  );
}

function App() {
  const [taskId, setTaskId] = useState(null);
  const [taskResult, setTaskResult] = useState(null);

  const handleTaskCreated = (newTaskId) => {
    console.log('New task created:', newTaskId);
    setTaskId(newTaskId);
  };

  const handleTaskCompleted = (result) => {
    console.log('Task completed:', result);
    setTaskResult(result);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Meshy Image to 3D Converter</h1>
        <p>Upload an image to create a 3D model using Meshy's API.</p>
        <ErrorBoundary>
          <ImageUploader onTaskCreated={handleTaskCreated} />
          {taskId && <TaskStatus taskId={taskId} onTaskCompleted={handleTaskCompleted} />}
          {taskResult && (
            <div>
              <h2>3D Model Created!</h2>
              <p>Download URL: <a href={taskResult.download_url} target="_blank" rel="noopener noreferrer">Download 3D Model</a></p>
            </div>
          )}
        </ErrorBoundary>
      </header>
    </div>
  );
}

export default App;
