// src/components/TaskStatus.js
import React, { useState, useEffect } from 'react';
import { getImageTo3DTask } from '../services/meshyApi';

function TaskStatus({ taskId, onTaskCompleted, onProgressUpdate }) {
  const [task, setTask] = useState(null);

  useEffect(() => {
    const checkStatus = async () => {
      try {
        const taskData = await getImageTo3DTask(taskId);
        setTask(taskData);

        if (taskData.status === 'SUCCEEDED') {
          onTaskCompleted(taskData);
        } else {
          // Update progress based on the status
          const progressPercentage = taskData.progress || 0;
          onProgressUpdate(progressPercentage);

          // Continue checking status if not completed
          if (taskData.status !== 'FAILED' && taskData.status !== 'EXPIRED') {
            setTimeout(checkStatus, 5000); // Check every 5 seconds
          }
        }
      } catch (error) {
        console.error('Error checking task status:', error);
      }
    };

    checkStatus();
  }, [taskId, onTaskCompleted, onProgressUpdate]);

  if (!task) {
    return <div>Loading task status...</div>;
  }

  return (
    <div className="task-status">
      <h3>Task Status: {task.status}</h3>
      <p>Progress: {task.progress}%</p>
      {task.status === 'SUCCEEDED' && (
        <div className="download-links">
          <p>3D Model is ready!</p>
          <a href={task.model_urls.glb} target="_blank" rel="noopener noreferrer">Download GLB</a>
          <a href={task.model_urls.fbx} target="_blank" rel="noopener noreferrer">Download FBX</a>
          <a href={task.model_urls.usdz} target="_blank" rel="noopener noreferrer">Download USDZ</a>
        </div>
      )}
    </div>
  );
}

export default TaskStatus;
