import axios from 'axios';

const API_BASE_URL = 'https://api.meshy.ai/v1';
const API_KEY = process.env.REACT_APP_MESHY_API_KEY;

const meshyApi = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Authorization': `Bearer ${API_KEY}`,
    'Content-Type': 'application/json',
  },
});

export const createImageTo3DTask = async (imageUrl) => {
  const response = await meshyApi.post('/image-to-3d', {
    image_url: imageUrl,
    enable_pbr: true,
    ai_model: 'meshy-4',
  });
  return response.data.result;
};

export const getImageTo3DTask = async (taskId) => {
  const response = await meshyApi.get(`/image-to-3d/${taskId}`);
  return response.data;
};
